mapper.select:
select oi.id, oi.consignee, oi.consignee_tel, oi.total_amount, oi.order_status, oi.user_id, oi.payment_way, oi.delivery_address, oi.order_comment, oi.out_trade_no, oi.trade_body, oi.create_time, oi.expire_time, oi.process_status, od.id detail_id, od.order_id, od.sku_id, od.sku_name, od.img_url, od.order_price, od.sku_num, od.create_time from order_info oi inner join order_detail od on oi.id = od.order_id
user_id = #{userId}
and oi.order_status = #{orderStatus}
ORDER BY oi.id DESC
